    private void jsalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jsalvarActionPerformed
try{
        Class.forName("com.mysql.jdbc.Driver");
        Connection conexao=DriverManager.getConnection
        ("jdbc:mysql://localhost/aula17","root","");
        Statement sql=conexao.createStatement();
        String nome=jnome.getText();
        String quantidade=jquantidade.getText();
        String unidade=junidade.getText();
        String total=jtotal.getText();
        
      
        sql.executeUpdate("insert into produto (nome,quantidade,unidade,total)values('"+nome+"','"+quantidade+"','"+unidade+"','"+total+"')");
        JOptionPane.showMessageDialog(this,"Cadastrado com sucesso");
        limpacampo();
        
        
    }  
    catch(ClassNotFoundException e){
        JOptionPane.showMessageDialog
        (this,"Erro na conexão" + e.getMessage());
    }
    catch(SQLException e){
        JOptionPane.showMessageDialog
        (this,"Erro na linguagem" + e.getMessage());
    }        // TODO add your handling code here:
    }//GEN-LAST:event_jsalvarActionPerformed

    private void jeditarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jeditarActionPerformed
try{
        Class.forName("com.mysql.jdbc.Driver");
        Connection conexao=DriverManager.getConnection
        ("jdbc:mysql://localhost/aula17","root","");
        Statement sql=conexao.createStatement();
        
        String nome=jnome.getText();
        String quantidade=jquantidade.getText();
        String unidade=junidade.getText();
        String total=jtotal.getText();
        
        int registro=sql.executeUpdate("update funcionario set nome='"+nome+"',quantidade='"+quantidade+"',unidade='"+unidade+"',total='"+total+"'");
        
        if(registro!=0)
        {
            JOptionPane.showMessageDialog(this,"Dados Alterados");
        }
        else
        {
            JOptionPane.showMessageDialog(this,"Dados não Alterados");
        }
    }  
        
    catch(ClassNotFoundException e){
        JOptionPane.showMessageDialog
        (this,"Erro na conexão" + e.getMessage());
    }
    catch(SQLException e){
        JOptionPane.showMessageDialog
        (this,"Erro na linguagem" + e.getMessage());
    }        // TODO add your handling code here:
    }//GEN-LAST:event_jeditarActionPerformed

    private void jexcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jexcluirActionPerformed
    try{
        Class.forName("com.mysql.jdbc.Driver");
        Connection conexao=DriverManager.getConnection
        ("jdbc:mysql://localhost/aula17","root","");
        Statement sql=conexao.createStatement();
        
        String nome=jnome.getText();
        
        int registro=sql.executeUpdate("delete from produto where nome='"+nome+"'");
        
        if(registro!=0)
        {
             JOptionPane.showMessageDialog(this,"Dados excluídos");
             limpacampo();
        }
        else
        {
            JOptionPane.showMessageDialog(this,"Dados não excluídos");
        }
        
    }  
    catch(ClassNotFoundException e){
        JOptionPane.showMessageDialog
        (this,"Erro na conexão" + e.getMessage());
    }
    catch(SQLException e){
        JOptionPane.showMessageDialog
        (this,"Erro na linguagem" + e.getMessage());
    }   // TODO add your handling code here:
    }//GEN-LAST:event_jexcluirActionPerformed

