    private void jconsultarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jconsultarActionPerformed
 boolean consulta=true;
        try{
        Class.forName("com.mysql.jdbc.Driver");
        Connection conexao=DriverManager.getConnection
        ("jdbc:mysql://localhost/aula17","root","");
        Statement sql=conexao.createStatement();
        
        String nome=jnome.getText();
        ResultSet linha=sql.executeQuery("select * from produto where jnome='"+nome+"'");
        
        while(linha.next())
        {   
            jnome.setText(linha.getString("nome"));
            jquantidade.setText(linha.getString("quantidade"));
            junidade.setText(linha.getString("unidade"));
            jtotal.setText(linha.getString("total"));
            int matricula=linha.getInt("matricula");
            jmatricula.setText(String.valueOf(matricula));
           
            
            consulta=false;
            JOptionPane.showMessageDialog(this,"Dados encontrados" );
        }
        if (consulta)
        {
            JOptionPane.showMessageDialog(this,"Dados não encontrados" );
            limpacampo();
        }
        
    }  
    catch(ClassNotFoundException e){
        JOptionPane.showMessageDialog
        (this,"Erro na conexão" + e.getMessage());
    }
    catch(SQLException e){
        JOptionPane.showMessageDialog
        (this,"Erro na linguagem" + e.getMessage());
    }                                              
       // TODO add your handling code here:
    }//GEN-LAST:event_jconsultarActionPerformed

